import React, { Component } from 'react';
import { FlatList, Text, View, KeyboardAvoidingView, StyleSheet, TouchableOpacity, Dimensions } from 'react-native';
import { ListItem } from 'react-native-elements';
import Moment from 'moment'
import { connect } from 'react-redux';
import { CreditCardInput } from "react-native-credit-card-fullpage-form";
import RBSheet from "react-native-raw-bottom-sheet";
import styles from './styles/checkOutCstyles';
import ExpoStripePurchase from 'expo-stripe-webview';

class CheckoutScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalVisible: false,
      placeholders: { number: "XXXX XXXX XXXX XXXX", expiry: "MM/YY", cvc: "CVC" },
      labels: { number: "CARD NUMBER", expiry: "EXPIRY", cvc: "CVC/CCV" }
    }
  };

  setModalVisible = () => {
    this.setState({ modalVisible: !this.state.modalVisible });
  }

  onClose = () => {
    // handle close (i.e. navigate back)
}

onPaymentSuccess = (token) => {
    // handle saving token on backend
    // will automatically call 'onClose'
}


  onChangeForm = (form) => {
    console.log(form)
  }

  renderItem = ({ item }) => {
    return (
      <View>
        <ListItem style={styles}>
          <ListItem.Title style={styles.leftText}>Barber:</ListItem.Title>
          <ListItem.Content>
          </ListItem.Content>
          <Text style={styles.rightText}>{item.barber}
          </Text>
        </ListItem>
        <ListItem >
          <ListItem.Title style={styles.leftText}>Date:</ListItem.Title>
          <ListItem.Content >
          </ListItem.Content>
          <Text style={styles.rightText}>
            {Moment(new Date(item.slot.date)).format("ddd DD MMM YYYY")}
          </Text>
        </ListItem>
        <ListItem bottomDivider>
          <ListItem.Title style={styles.leftText}>Slot:</ListItem.Title>
          <ListItem.Content>
          </ListItem.Content>
          <Text style={styles.rightText}>
            {item.slot.startTime} - {item.slot.endTime}
          </Text>
        </ListItem>
        {item.service.map((s) => (
          <ListItem style={styles}>
            <ListItem.Title style={styles.leftText}>x1  {s.Name}</ListItem.Title>
            <ListItem.Content>
            </ListItem.Content>
            <Text style={styles.rightText}>£{s.Price.toFixed(2)}
            </Text>
          </ListItem>
        ))
        }
        <ListItem topDivider>
          <ListItem.Title style={styles.leftText}>Total:</ListItem.Title>
          <ListItem.Content>
          </ListItem.Content>
          <Text style={styles.rightText}>
            £{item.total.toFixed(2)}
          </Text>
        </ListItem>
      </View>)
  }
  render() {

    const windowHeight = Dimensions.get('window').height;

    return (

      <View styles={styles.container}>

      <ExpoStripePurchase
      publicKey="FILL IN WITH YOUR DATA"
      amount={100000}
      imageUrl="www.clever-image-url.com"
      storeName="Clever Store Name"
      description="Clever product description."
      currency="USD"
      allowRememberMe={true}
      prepopulatedEmail="clever_email@clever.com"
      onClose={this.onClose}
      onPaymentSuccess={(token) => this.onPaymentSuccess(token)}
      style={{width: 1000, alignSelf: 'center'}}
  />
      </View>
    )
  }
}

const mapStatetoProps = (state) => {
  return {
    orders: state
  }
}

export default connect(mapStatetoProps)(CheckoutScreen);
